import React, { useRef, useState } from "react";
import Webcam from "react-webcam";
import axios from "axios";

const App = () => {
  const webcamRef = useRef(null);
  const [emotion, setEmotion] = useState("");

  const capture = async () => {
    const imageSrc = webcamRef.current.getScreenshot();

    const blob = await (await fetch(imageSrc)).blob();
    const file = new File([blob], "image.jpg", { type: "image/jpeg" });

    const formData = new FormData();
    formData.append("image", file);

    const response = await axios.post("http://localhost:5000/predict", formData);
    const data = response.data.emotions;

    if (data.length > 0) setEmotion(data[0].label);
    else setEmotion("No face detected");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 flex items-center justify-center p-6">
      <div className="bg-white rounded-3xl shadow-2xl p-8 max-w-md w-full text-center">
        <h1 className="text-2xl font-bold text-gray-800 mb-6">ðŸ§  Real-Time Emotion Detector</h1>

        <div className="rounded-xl overflow-hidden border-4 border-indigo-400 inline-block shadow-md">
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            width={320}
            height={240}
          />
        </div>

        <button
          onClick={capture}
          className="mt-6 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-6 rounded-full transition duration-300 shadow-lg"
        >
          Capture & Predict
        </button>

        <div className="mt-4 text-lg text-gray-700">
          <span className="font-semibold">Emotion:</span>{" "}
          <span className="text-indigo-600 font-bold">{emotion || "Waiting..."}</span>
        </div>
      </div>
    </div>
  );
};

export default App;
